// Сборка всех компонентов

import React from "react"
import Header from "./components/Header"
import Main from "./components/Main"
import Image from "./components/Image"



class App extends React.Component {
	helpText = "Help Text !"
	render() {
		return (<div className="app">
			<Header title="Шапка этого сайта" name="" />
			<Main title="Шапка этого сайта" name="" />

			<h1>{this.helpText}.</h1>
			<input placeholder={this.helpText}
				// добавляем пару обработчиков событий через функцию
				onClick={this.inputClick} onMouseEnter={this.mouseOver} />
			{/* Если наша переменная содержит слово Help Text ! тогда выводим Yes  */}
			<p>{this.helpText === "Help Text !" ? "Yes" : "No"}</p>
			<Image image="img_1.JPEG" />
		</div>)
	}
	inputClick() { console.log("Clicked") }
	mouseOver() { console.log("Mouse Over") }
}

export default App
