// Главный корневой компонент приложения, который собирает все компоненты вместе
import React from "react"
import Header from "./components/Header" // Импорт компонента шапки
import Users from "./components/Users"
import AddUser from "./components/AddUser"
import axios from "axios"

// ✅ Использовать публичный API который не требует аутентизации
const baseUrl = "https://jsonplaceholder.typicode.com/users"


// Классовый компонент App - основной компонент приложения
class App extends React.Component {
	constructor( props ) {
		super( props )

		axios.get( baseUrl ).then( ( res ) => {
			console.log( res.data )
		} )

		this.state = {
			users: [

			]
		}
		this.addUser = this.addUser.bind( this )

		this.deleteUser = this.deleteUser.bind( this )

		this.editUser = this.editUser.bind( this )
	}


	// Основной метод рендеринга компонента
	render() {
		return (
			<div>
				<Header title="Список пользователей" />
				<main>
					<Users users={this.state.users} onEdit={this.editUser} onDelete={this.deleteUser} />
				</main>
				<aside>
					<AddUser onAdd={this.addUser} />
				</aside>
			</div>
		)
	}

	deleteUser( id ) {
		this.setState( {
			users: this.state.users.filter( ( el ) => el.id !== id )
		} )
	}

	editUser( user ) {
		let allUsers = this.state.users
		allUsers[user.id - 1] = user

		this.setState( { users: [] }, () => {
			this.setState( { users: [...allUsers] } )
		} )

	}

	addUser( user ) {
		const id = this.state.users.length + 1
		this.setState( { users: [...this.state.users, { id, ...user }] } )

	}
}

// Экспорт компонента для использования в качестве корневого
export default App
