import React from "react"
import Header from "./components/Header"
import Users from "./components/Users"
import AddUser from "./components/AddUser"

class App extends React.Component {
	constructor( props ) {
		super( props )
		this.state = {
			users: [
				{
					id: 1,
					firstname: 'Lulu',
					lastname: 'Pinachet',
					bio: 'sovsem nitacoi kak vse',
					age: 40,
					isHappy: true
				},
				{
					id: 2,
					firstname: 'Balu',
					lastname: 'Zalupkin',
					bio: 'sovsem nitacoi kak vse i dashe bolhe',
					age: 23,
					isHappy: false
				}
			]
		}
	}

	// ✅ Оптимальная генерация уникального ID
	addUser = ( user ) => {
		const newId = this.generateUniqueId()

		this.setState( prevState => ( {
			users: [...prevState.users, { id: newId, ...user }]
		} ) )
	}

	// ✅ Вспомогательный метод для генерации ID
	generateUniqueId = () => {
		return this.state.users.length > 0
			? Math.max( ...this.state.users.map( u => u.id ) ) + 1
			: 1
	}

	// ✅ Метод для удаления пользователя (дополнительная функциональность)
	deleteUser = ( userId ) => {
		this.setState( prevState => ( {
			users: prevState.users.filter( user => user.id !== userId )
		} ) )
	}

	render() {
		const { users } = this.state

		return (
			<div className="app">
				<Header title="Список пользователей" />

				<div className="app-content">
					<main className="app-main">
						<Users
							users={users}
							onDeleteUser={this.deleteUser} // ✅ Передаем функцию удаления
						/>
					</main>

					<aside className="app-sidebar">
						<AddUser onAdd={this.addUser} />
					</aside>
				</div>
			</div>
		)
	}
}

export default App
