import React from "react"
import { IoCloseCircleSharp, IoHammerSharp } from 'react-icons/io5'
import { FaAccessibleIcon } from "react-icons/fa"

class User extends React.Component {
	// ✅ Обработчик клика по иконке удаления
	handleDelete = () => {
		if ( this.props.onDelete ) {
			this.props.onDelete( this.props.user.id )
		}
	}

	// ✅ Обработчик клика по иконке редактирования
	handleEdit = () => {
		if ( this.props.onEdit ) {
			this.props.onEdit( this.props.user )
		}
	}

	render() {
		const { user } = this.props

		// ✅ Проверка на наличие пользователя
		if ( !user ) {
			return <div className="user">Пользователь не найден</div>
		}

		return (
			<div className="user">
				{/* Иконка удаления с обработчиком */}
				<IoCloseCircleSharp
					className="delete-icon"
					onClick={this.handleDelete}
					title="Удалить пользователя"
				/>

				{/* Иконка редактирования с обработчиком */}
				<IoHammerSharp
					className="edit-icon"
					onClick={this.handleEdit}
					title="Редактировать пользователя"
				/>

				{/* Иконка доступности */}
				<FaAccessibleIcon
					className="style-icon"
					title="Информация о доступности"
				/>

				{/* Данные пользователя */}
				<h3>{user.firstname} {user.lastname}</h3>
				<p className="user-bio">{user.bio}</p>
				<p className="user-age">Возраст: {user.age}</p>
				<b className="user-status">
					{user.isHappy ? 'Счастлив :)' : 'Не особо :('}
				</b>
			</div>
		)
	}
}

// ✅ Значения по умолчанию для props
User.defaultProps = {
	user: {},
	onDelete: null,
	onEdit: null
}

export default User
