// Сборка всех компонентов

import React from "react"
import Header from "./components/Header"
import Main from "./components/Main"
import CustomImage from "./components/Image"
import Logo from "./img/img_1.jpeg"

class App extends React.Component {
	constructor(props) {
		super(props)
		this.state = {
			helpText: "Help Text",
		}
		// Ошибка 1: Нужно привязать оба метода
		this.inputClick = this.inputClick.bind(this)
		this.mouseOver = this.mouseOver.bind(this)
	}

	// Ошибка 2: Методы должны быть объявлены до render()
	inputClick() {
		this.setState({ helpText: "Changed" })
		console.log("Clicked")
	}

	mouseOver() {
		console.log("Mouse Over")
	}

	render() {
		return (
			<div className="app">
				<Header title="Шапка сайта" />
				<Main title="Основной контент" />

				<h1>{this.state.helpText}.</h1>
				{/* Ошибка 3: Нужно обращаться к методам через this, а не через this.state */}
				<input
					placeholder={this.state.helpText}
					onClick={this.inputClick}
					onMouseEnter={this.mouseOver}
				/>
				{/* Ошибка 4: Нужно обращаться к state, а не к несуществующему свойству helpText */}
				<p>{this.state.helpText === "Help Text" ? "Yes" : "No"}</p>
				<CustomImage image={Logo} />
			</div>
		)
	}
}

export default App
