// Дополнительные улучшения (опционально):
import React from "react"

class AddUser extends React.Component {
	constructor( props ) {
		super( props )
		this.state = {
			firstName: "", // ✅ Стандартное именование
			lastName: "",  // ✅ Стандартное именование
			bio: "",
			age: "",
			isHappy: false
		}
	}

	// ✅ Отдельный метод для обработки отправки
	handleSubmit = () => {
		const userData = {
			firstName: this.state.firstName,
			lastName: this.state.lastName,
			bio: this.state.bio,
			age: parseInt( this.state.age ) || 0,
			isHappy: this.state.isHappy,
		};

		this.props.onAdd( userData );
		this.resetForm();
	}

	// ✅ Метод для сброса формы
	resetForm = () => {
		this.setState( {
			firstName: "",
			lastName: "",
			bio: "",
			age: "",
			isHappy: false
		} );
	}

	render() {
		return (
			<form onSubmit={( e ) => e.preventDefault()}>
				{/* ... поля формы ... */}
				<button type="button" onClick={this.handleSubmit}>
					Добавить
				</button>
			</form>
		)
	}
}

export default AddUser
