//  Однин из подключаемых компонентов

import React from "react"

class AddUser extends React.Component {
	userAdd = {}
	constructor( props ) {
		super( props )
		this.state = {
			name: this.props.user ? this.props.user.name : "",
			username: this.props.user ? this.props.user.username : "",
			email: this.props.user ? this.props.user.email : "",
			street: this.props.user && this.props.user.address ? this.props.user.address.street : "",
			city: this.props.user && this.props.user.address ? this.props.user.address.city : "",
			suite: this.props.user && this.props.user.address ? this.props.user.address.suite : "",
			zipcode: this.props.user && this.props.user.address ? this.props.user.address.zipcode : "",
			isHappy: this.props.user ? this.props.user.isHappy : false
		}
	}

	render() {
		return (
			<form ref={( el ) => this.myForm = el}>
				<h4>Имя, Фамилия, Мыло</h4>
				<input
					placeholder="Имя"
					value={this.state.name}
					onChange={( e ) => this.setState( { name: e.target.value } )}
				/>
				<input
					placeholder="Никнейм"
					value={this.state.username}
					onChange={( e ) => this.setState( { username: e.target.value } )}
				/>
				<input
					placeholder="Емайл"
					type="email"
					value={this.state.email}
					onChange={( e ) => this.setState( { email: e.target.value } )}
				/>

				{/* ✅ Раздельные поля для адреса */}
				<h4>Адрес:</h4>
				<input
					placeholder="Улица"
					value={this.state.street}
					onChange={( e ) => this.setState( { street: e.target.value } )}
				/>
				<input
					placeholder="Город"
					value={this.state.city}
					onChange={( e ) => this.setState( { city: e.target.value } )}
				/>
				<input
					placeholder="Квартира/Офис"
					value={this.state.suite}
					onChange={( e ) => this.setState( { suite: e.target.value } )}
				/>
				<input
					placeholder="Почтовый индекс"
					value={this.state.zipcode}
					onChange={( e ) => this.setState( { zipcode: e.target.value } )}
				/>

				<label htmlFor="isHappy">
					<input
						type="checkbox"
						id="isHappy"
						checked={this.state.isHappy}
						onChange={( e ) => this.setState( { isHappy: e.target.checked } )}
					/>
					Счастлив?
				</label>

				<button type="button" onClick={() => {
					this.myForm.reset()

					// ✅ Создаем объект адреса правильно
					this.userAdd = {
						name: this.state.name,
						username: this.state.username,
						email: this.state.email,
						address: {
							street: this.state.street,
							city: this.state.city,
							suite: this.state.suite,
							zipcode: this.state.zipcode,
							geo: {
								lat: "0",
								lng: "0"
							}
						},
						isHappy: this.state.isHappy,
					}

					if ( this.props.user ) {
						this.userAdd.id = this.props.user.id
						// ✅ Сохраняем существующие данные если они есть
						if ( this.props.user.address && this.props.user.address.geo ) {
							this.userAdd.address.geo = this.props.user.address.geo
						}
					}

					this.props.onAdd( this.userAdd )

					// ✅ Очищаем форму после отправки
					if ( !this.props.user ) {
						this.setState( {
							name: "",
							username: "",
							email: "",
							street: "",
							city: "",
							suite: "",
							zipcode: "",
							isHappy: false
						} )
					}
				}}>
					{this.props.user ? 'Обновить' : 'Добавить'}
				</button>
			</form>
		)
	}
}

export default AddUser
