//  Однин из подключаемых компонентов

import React from "react"
import { IoCloseCircleSharp, IoHammerSharp } from 'react-icons/io5'
import { FaAccessibleIcon } from "react-icons/fa"
import AddUser from "./AddUser"

// ✅ Импортируем все 5 картинок
import userImage1 from '../img/img_1.jpeg'
import userImage2 from '../img/img_2.jpeg'
import userImage3 from '../img/img_3.jpeg'
import userImage4 from '../img/img_4.jpeg'
import userImage5 from '../img/img_5.jpeg'

class User extends React.Component {
	constructor( props ) {
		super( props )
		this.state = {
			editForm: false,
			showImage: false
		}

		// ✅ Массив со всеми картинками
		this.userImages = [userImage1, userImage2, userImage3, userImage4, userImage5]

		// ✅ Генерируем случайную картинку для пользователя на основе ID
		this.userRandomImage = this.getRandomImageForUser()
	}

	user = this.props.user

	// ✅ Функция для получения случайной картинки на основе ID пользователя
	getRandomImageForUser = () => {
		// Используем ID пользователя как seed для псевдослучайности
		// Это гарантирует, что у одного пользователя всегда будет одна и та же картинка
		const randomIndex = this.user.id % this.userImages.length
		return this.userImages[randomIndex]
	}

	// ✅ Альтернативная функция для полностью случайной картинки
	getCompletelyRandomImage = () => {
		const randomIndex = Math.floor( Math.random() * this.userImages.length )
		return this.userImages[randomIndex]
	}

	// Функция для показа/скрытия картинки
	toggleImage = () => {
		this.setState( {
			showImage: !this.state.showImage
		} )
	}

	render() {
		return (
			<div className="user">
				<IoCloseCircleSharp onClick={() => this.props.onDelete( this.user.id )} className="delete-icon" />
				<IoHammerSharp onClick={() => {
					this.setState( {
						editForm: !this.state.editForm
					} )
				}} className="edit-icon" />
				<FaAccessibleIcon onClick={this.toggleImage} className="style-icon" title="Показать/скрыть изображение" />

				<h3>{this.user.firstname} {this.user.lastname}</h3>
				<p>{this.user.bio}</p>
				<b>{this.user.isHappy ? 'Счастлив :)' : 'Не особо :('}</b>

				{this.state.showImage && (
					<div className="user-image-container">
						<img
							src={this.userRandomImage} // ✅ Используем случайную картинку пользователя
							alt={`${this.user.firstname} ${this.user.lastname}`}
							className="user-image"
							onError={( e ) => {
								// ✅ Запасной вариант на случай ошибки
								e.target.style.display = 'none'
							}}
						/>
						<button
							onClick={this.toggleImage}
							className="close-image-btn"
						>
							Закрыть
						</button>
					</div>
				)}

				{this.state.editForm && <AddUser user={this.user} onAdd={this.props.onEdit} />}
			</div>
		)
	}
}

export default User
