//  Однин из подключаемых компонентов

import React, { useState } from "react"

// Применяем через функцию
const Button = ( { text = "Кнопка", ...props } ) => {
	const click = useState( 3 )
	console.log( click )

	return (
		<button {...props}>{text}</button>
	)
}


Button.defaultProps = {
	text: "Кнопка"
}

export default Button
