// Сборка всех компонентов

import React from "react"
import Header from "./components/Header"
import Main from "./components/Main"
import CustomImage from "./components/Image"
import Logo from "./img/img_1.png"
import Dogo from "./img/img_2.png"
import Fogo from "./img/img_3.png"

// Создаем классовый компонент App, наследующийся от React.Component
class App extends React.Component {
	// Конструктор компонента, вызывается при создании компонента
	constructor(props) {
		super(props) // Вызываем конструктор родительского класса React.Component
		
		// Инициализируем состояние компонента
		this.state = {
			helpText: "Help Text", // Начальное значение для текста помощи
			userData: "" // Начальное значение для данных пользователя (пустая строка)
		}
		
		// Привязываем методы к контексту текущего компонента
		// Это необходимо, чтобы внутри методов this ссылался на компонент
		this.inputClick = this.inputClick.bind(this)
		this.mouseOver = this.mouseOver.bind(this)
	}

	// Метод для обработки клика по input
	inputClick() {
		// Изменяем состояние компонента - обновляем helpText
		this.setState({ helpText: "Changed" })
		console.log("Clicked") // Вывод в консоль для отладки
	}
	
	// Метод для обработки наведения мыши на input
	mouseOver() { 
		console.log("Mouse Over") // Вывод в консоль для отладки
	}

	
	// Обязательный метод render - возвращает JSX разметку компонента
	render() {
		return (
			<div className="app">
				{/* Компонент Header с передачей пропса title */}
				<Header title="Шапка сайта" />
				
				{/* Компонент Main с передачей пропса title */}
				<Main title="Основной контент" />

				{/* Отображаем текущее значение helpText из состояния */}
				<h1>{this.state.helpText}.</h1>
				
				{/* Отображаем текущее значение userData из состояния */}
				<h2>{this.state.userData}.</h2>
				
				{/* Поле ввода с обработчиками событий */}
				<input
					placeholder={this.state.helpText} // Плейсхолдер из состояния
					// Обработчик изменения значения - обновляет userData в реальном времени
					onChange={event => this.setState({userData: event.target.value})}
					// Обработчик клика - вызывает метод inputClick
					onClick={this.inputClick}
					// Обработчик наведения мыши - вызывает метод mouseOver
					onMouseEnter={this.mouseOver}
				/>
				
				{/* Условный рендеринг: проверяем равно ли helpText исходному значению */}
				<p>{this.state.helpText === "Help Text" ? "Yes" : "No"}</p>
				
				{/* Компонент для отображения изображения с передачей пропса image */}
				<CustomImage image={Logo} />
				<CustomImage image={Dogo} />
				<CustomImage image={Fogo} />
			</div>
		)
	}
}

export default App


// Пояснения к ключевым моментам:

// Состояние (state) - внутренние данные компонента, которые могут изменяться

// helpText - текст, который отображается и меняется при клике

// userData - хранит введенные пользователем данные

// Привязка методов - в конструкторе методы привязываются к контексту компонента, чтобы this работал корректно

// Обработчики событий:

// onChange - срабатывает при каждом изменении значения input

// onClick - при клике на input

// onMouseEnter - при наведении курсора на input

// Условный рендеринг - тернарный оператор показывает "Yes" или "No" в зависимости от значения helpText

// Работа с состоянием:

// this.setState() - асинхронный метод для обновления состояния

// При обновлении состояния компонент перерисовывается

// Поток данных:

// Пользователь вводит текст → onChange → обновляется userData → перерисовывается компонент

// Пользователь кликает на поле → onClick → обновляется helpText → перерисовывается компонент















