//  Однин из подключаемых компонентов

// Импортируем React и хук useState для управления состоянием компонента
import React, { useEffect, useState } from "react"

// Функциональный компонент Button с деструктуризацией props
// text - текст кнопки (по умолчанию "Кнопка")
// ...props - все остальные props, которые будут переданы кнопке
const Button = ( { text = "Кнопка", ...props } ) => {
	// Используем хук useState для создания состояния click
	// click - текущее значение счетчика кликов
	// setClick - функция для обновления значения счетчика
	// 0 - начальное значение счетчика
	const [click, setClick] = useState( 0 )

	useEffect( () => {
		document.title = `Вы нажали ${click}`
	} )

	// Возвращаем JSX разметку кнопки
	return (
		<button
			// Обработчик клика по кнопке:
			// При каждом клике увеличиваем счетчик на 1
			onClick={() => setClick( click + 1 )}

			// Распаковываем все остальные props (className, disabled, id и т.д.)
			{...props}
		>
			{/* Отображаем текст кнопки и текущее количество кликов */}
			{text}{click}
		</button>
	)
}

// Запасной вариант значений по умолчанию через defaultProps
// (альтернатива деструктуризации в параметрах функции)
Button.defaultProps = {
	text: "Кнопка"
}

// Экспортируем компонент для использования в других файлах
export default Button



// Как работает компонент:
// Принимает props: text (текст кнопки) и любые другие HTML-атрибуты

// Считает клики: при каждом клике увеличивает счетчик на 1

// Отображает: текст кнопки + количество кликов

// Пример использования: <Button text="Нажми меня" className="btn" />

// Результат: "Нажми меня0", "Нажми меня1", "Нажми меня2" и т.д.
