// Главный корневой компонент приложения, который собирает все компоненты вместе
import React from "react"
import Header from "./components/Header" // Импорт компонента шапки


// Классовый компонент App - основной компонент приложения
class App extends React.Component {


	// Основной метод рендеринга компонента
	render() {
		return (
			// Основной контейнер с классом "name"
			<div className="name">
				{/* Компонент шапки с передачей пропсов */}
				<Header title="Шапка сайта" name="" />

				{/* Заголовок с текстом из состояния helpText */}
				<h1>{this.state.helpText}</h1>

				{/* Подзаголовок с данными пользователя из состояния userData */}
				<h2>{this.state.userData}</h2>

				{/* Поле ввода с обработчиками событий */}
				<input
					placeholder={this.state.helpText} // Плейсхолдер с текстом подсказки

					// Обработчик изменения значения в input
					// Обновляет состояние userData при каждом изменении текста
					onChange={event => this.setState( { userData: event.target.value } )}

					// Обработчик клика по полю ввода
					onClick={this.inputClick}

					// Обработчик наведения мыши на поле ввода
					onMouseEnter={this.mouseOver}
				/>

				{/* Параграф с условным рендерингом */}
				{/* Если helpText равен "Help text!" показываем "Yes", иначе "No" */}
				<p>{this.state.helpText === "Help text!" ? "Yes" : "No"}</p>

				{/* Кастомный компонент изображения с передачей логотипа */}
				<CustomImage image={Logo} />

				{/* Стандартный тег img для логотипа компании */}
				<img src="logo.png" alt="Логотип компании" />
			</div>
		)
	}
}

// Экспорт компонента для использования в качестве корневого
export default App



