//  –û–¥–Ω–∏–Ω –∏–∑ –ø–æ–¥–∫–ª—é—á–∞–µ–º—ã—Ö –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–æ–≤

import React from "react"
import { IoCloseCircleSharp, IoHammerSharp, } from 'react-icons/io5'
import { FaAccessibleIcon } from "react-icons/fa";
import AddUser from "./AddUser";

// ‚úÖ –ò–º–ø–æ—Ä—Ç–∏—Ä—É–µ–º –≤—Å–µ 5 –∫–∞—Ä—Ç–∏–Ω–æ–∫
import userImage1 from '../img/img_1.jpeg'
import userImage2 from '../img/img_2.jpeg'
import userImage3 from '../img/img_3.jpeg'
import userImage4 from '../img/img_4.jpeg'
import userImage5 from '../img/img_5.jpeg'

class User extends React.Component {
	constructor( props ) {
		super( props )
		this.state = {
			editForm: false,
			showImage: false,
			avatarUrl: "" // ‚úÖ –î–æ–±–∞–≤–ª—è–µ–º —Å–æ—Å—Ç–æ—è–Ω–∏–µ –¥–ª—è –∞–≤–∞—Ç–∞—Ä–∞
		}

		// ‚úÖ –ú–∞—Å—Å–∏–≤ —Å–æ –≤—Å–µ–º–∏ –∫–∞—Ä—Ç–∏–Ω–∫–∞–º–∏
		this.userImages = [userImage1, userImage2, userImage3, userImage4, userImage5]

		// ‚úÖ –ì–µ–Ω–µ—Ä–∏—Ä—É–µ–º —Å–ª—É—á–∞–π–Ω—É—é –∫–∞—Ä—Ç–∏–Ω–∫—É –¥–ª—è –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –Ω–∞ –æ—Å–Ω–æ–≤–µ ID
		this.userRandomImage = this.getRandomImageForUser()
	}

	user = this.props.user

	// ‚úÖ –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø–æ–ª—É—á–µ–Ω–∏—è —Å–ª—É—á–∞–π–Ω–æ–π –∫–∞—Ä—Ç–∏–Ω–∫–∏ –Ω–∞ –æ—Å–Ω–æ–≤–µ ID –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
	getRandomImageForUser = () => {
		const randomIndex = this.user.id % this.userImages.length
		return this.userImages[randomIndex]
	}

	// ‚úÖ –§—É–Ω–∫—Ü–∏—è –¥–ª—è –≥–µ–Ω–µ—Ä–∞—Ü–∏–∏ –∞–≤–∞—Ç–∞—Ä–∞ –Ω–∞ –æ—Å–Ω–æ–≤–µ –∏–º–µ–Ω–∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
	generateAvatar = () => {
		const avatarStyles = [
			'avataaars', 'micah', 'adventurer', 'big-ears', 'bottts',
			'fun-emoji', 'lorelei', 'notionists', 'open-peeps'
		]

		const randomStyle = avatarStyles[Math.floor( Math.random() * avatarStyles.length )]
		const seed = this.user.name || this.user.username || 'user'

		return `https://api.dicebear.com/7.x/${randomStyle}/svg?seed=${encodeURIComponent( seed )}`
	}

	componentDidMount() {
		// ‚úÖ –ì–µ–Ω–µ—Ä–∏—Ä—É–µ–º –∞–≤–∞—Ç–∞—Ä –ø—Ä–∏ –º–æ–Ω—Ç–∏—Ä–æ–≤–∞–Ω–∏–∏ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞
		this.setState( {
			avatarUrl: this.generateAvatar()
		} )
	}

	// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø–æ–∫–∞–∑–∞/—Å–∫—Ä—ã—Ç–∏—è –∫–∞—Ä—Ç–∏–Ω–∫–∏
	toggleImage = () => {
		this.setState( {
			showImage: !this.state.showImage
		} )
	}

	render() {
		return (
			<div className="user">
				<IoCloseCircleSharp onClick={() => this.props.onDelete( this.user.id )} className="delite-icon" />
				<IoHammerSharp onClick={() => {
					this.setState( {
						editForm: !this.state.editForm
					} )
				}} className="edit-icon" />
				<FaAccessibleIcon onClick={this.toggleImage} className="style-icon" title="–ü–æ–∫–∞–∑–∞—Ç—å/—Å–∫—Ä—ã—Ç—å –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ" />

				{/* ‚úÖ –û—Ç–æ–±—Ä–∞–∂–∞–µ–º –∞–≤–∞—Ç–∞—Ä - –ï–î–ò–ù–°–¢–í–ï–ù–ù–û–ï –ò–ó–ú–ï–ù–ï–ù–ò–ï */}
				<div className="user-avatar">
					<img
						src={this.state.avatarUrl}
						alt={`–ê–≤–∞—Ç–∞—Ä ${this.user.name}`}
						className="avatar-image"
						onError={( e ) => {
							e.target.src = this.userRandomImage
						}}
					/>
				</div>

				<h3>{this.user.name}</h3>

				<h3>{this.user.username}</h3>

				<p>{this.user.email}</p>

				<p>üìç {this.user.address && `${this.user.address.street}, ${this.user.address.city}`}</p>

				{this.user.address && (
					<div className="address-details">
						<small>
							{this.user.address.suite && `–ö–≤–∞—Ä—Ç–∏—Ä–∞: ${this.user.address.suite}`}<br />
							{this.user.address.zipcode && `–ò–Ω–¥–µ–∫—Å: ${this.user.address.zipcode}`}
						</small>
					</div>
				)}

				<b>{this.user.isHappy ? '–°—á–∞—Å—Ç–ª–∏–≤ :)' : '–ù–µ –æ—Å–æ–±–æ :('}</b>

				{this.state.showImage && (
					<div className="user-image-container">
						<img
							src={this.userRandomImage} // ‚úÖ –ò—Å–ø–æ–ª—å–∑—É–µ–º —Å–ª—É—á–∞–π–Ω—É—é –∫–∞—Ä—Ç–∏–Ω–∫—É
							alt={`${this.user.name} ${this.user.username}`}
							className="user-image"
							onError={( e ) => {
								// ‚úÖ –ó–∞–ø–∞—Å–Ω–æ–π –≤–∞—Ä–∏–∞–Ω—Ç –Ω–∞ —Å–ª—É—á–∞–π –æ—à–∏–±–∫–∏
								e.target.style.display = 'none'
							}}
						/>
						<button
							onClick={this.toggleImage}
							className="close-image-btn"
						>
							–ó–∞–∫—Ä—ã—Ç—å
						</button>
					</div>
				)}

				{this.state.editForm && <AddUser user={this.user} onAdd={this.props.onEdit} />}
			</div>
		)
	}
}

export default User
