//  Однин из подключаемых компонентов

import React from "react"

class AddUser extends React.Component {
	userAdd = {}
	constructor( props ) {
		super( props )
		this.state = {
			name: this.props.user ? this.props.user.name : "",
			username: this.props.user ? this.props.user.username : "",
			email: this.props.user ? this.props.user.email : "",
			address: this.props.user && this.props.user.address ?
				`${this.props.user.address.street}, ${this.props.user.address.city}` : "",
			isHappy: this.props.user ? this.props.user.isHappy : false
		}
	}

	render() {
		return (
			<form ref={( el ) => this.myForm = el}>
				{/* ... остальные поля ... */}

				<input
					placeholder="Адрес (Улица, Город)"
					value={this.state.address}
					onChange={( e ) => this.setState( { address: e.target.value } )}
				/>

				<button type="button" onClick={() => {
					this.myForm.reset()

					// ✅ Парсим адрес из строки
					const addressParts = this.state.address.split( ',' ).map( part => part.trim() )

					this.userAdd = {
						name: this.state.name,
						username: this.state.username,
						email: this.state.email,
						address: {
							street: addressParts[0] || "",
							city: addressParts[1] || "",
							suite: this.props.user && this.props.user.address ? this.props.user.address.suite : "",
							zipcode: this.props.user && this.props.user.address ? this.props.user.address.zipcode : "",
							geo: {
								lat: "0",
								lng: "0"
							}
						},
						isHappy: this.state.isHappy,
					}

					if ( this.props.user ) {
						this.userAdd.id = this.props.user.id
					}

					this.props.onAdd( this.userAdd )
				}}>
					{this.props.user ? 'Обновить' : 'Добавить'}
				</button>
			</form>
		)
	}
}

export default AddUser
