import React from "react"
import ReactDOM from "react-dom"

// Обращаемся к ДОМ странице (ReactDOM) и выбираем нужные объекты с помощью ( render() )
ReactDOM.render(
	// Добавляем функцию ввода и обработчики событий
	React.createElement('input', {
		placeholder: "Help Text",
		onClick: () => console.log("Clicked"),
		onMouseEnter: () => console.log("Mouse Over"),

	}),
	// Указываем объект куда будем выводить информацию с тегом id=""
	document.getElementById("app"));


// Используем JSX
ReactDOM.render(<input placeholder="Any Text" />, document.getElementById("root"));


// Сделаем тоже самое только через переменные

const inputClick = () => console.log("Clicked")
const mouseOver = () => console.log("Mouse Over")

// переменная ввода
const helpText = "Help Text !"

const elements =
	(<div className="code">
		<h1>Переменные через JSX</h1>
		<input placeholder={helpText}
			// добавляем пару обработчиков событий через функцию
			onClick={inputClick} onMouseEnter={mouseOver} />
		<p>{1 + 5}</p>
		{/* Если наша переменная содержит слово Help Text ! тогда выводим Yes  */}
		<p>{helpText === "Help Text !" ? "Yes" : "No"}</p>
	</div>)

const code = document.getElementById("code")

ReactDOM.render(elements, code);
