//  Однин из подключаемых компонентов

import React from "react"

const Button = ( {
	text = "Кнопка",
	onClick,
	disabled = false,
	type = "button",
	className = ""  // используем className вместо class
} ) => {
	return (
		<button
			type={type}
			onClick={onClick}
			disabled={disabled}
			className={className}  // правильный атрибут
		>
			{text}
		</button>
	)
}

export default Button
