// Главный корневой компонент приложения, который собирает все компоненты вместе
import React from "react"
import Header from "./components/Header" // Импорт компонента шапки
// import Main from "./components/Main" // Закомментированный импорт основного контента
import CustomImage from "./components/Image" // Импорт кастомного компонента изображения
import Logo from "./img/img_1.png" // Импорт изображения логотипа

// import Dogo from "./img/img_2.png" // Закомментированные дополнительные изображения
// import Fogo from "./img/img_3.png"

// Классовый компонент App - основной компонент приложения
class App extends React.Component {
	// Конструктор компонента для инициализации состояния
	constructor( props ) {
		super( props ) // Вызов конструктора родительского класса React.Component

		// Инициализация состояния компонента
		this.state = {
			helpText: "Help text", // Текст подсказки
			userData: "" // Данные, введенные пользователем
		}

		// Привязка контекста this для метода inputClick
		// Чтобы this внутри метода указывал на экземпляр компонента
		this.inputClick = this.inputClick.bind( this )
	}

	// Метод жизненного цикла - вызывается после обновления компонента
	componentDidUpdate( prevProv ) {
		// Проверяем, изменился ли helpText и не равен ли он "Help"
		if ( this.state.helpText !== "Help" )
			console.log( "Some" ); // Вывод в консоль при выполнении условия
	}

	// Основной метод рендеринга компонента
	render() {
		return (
			// Основной контейнер с классом "name"
			<div className="name">
				{/* Компонент шапки с передачей пропсов */}
				<Header title="Шапка сайта" name="" />

				{/* Заголовок с текстом из состояния helpText */}
				<h1>{this.state.helpText}</h1>

				{/* Подзаголовок с данными пользователя из состояния userData */}
				<h2>{this.state.userData}</h2>

				{/* Поле ввода с обработчиками событий */}
				<input
					placeholder={this.state.helpText} // Плейсхолдер с текстом подсказки

					// Обработчик изменения значения в input
					// Обновляет состояние userData при каждом изменении текста
					onChange={event => this.setState( { userData: event.target.value } )}

					// Обработчик клика по полю ввода
					onClick={this.inputClick}

					// Обработчик наведения мыши на поле ввода
					onMouseEnter={this.mouseOver}
				/>

				{/* Параграф с условным рендерингом */}
				{/* Если helpText равен "Help text!" показываем "Yes", иначе "No" */}
				<p>{this.state.helpText === "Help text!" ? "Yes" : "No"}</p>

				{/* Кастомный компонент изображения с передачей логотипа */}
				<CustomImage image={Logo} />

				{/* Стандартный тег img для логотипа компании */}
				<img src="logo.png" alt="Логотип компании" />
			</div>
		)
	}

	// Метод обработки клика по полю ввода
	inputClick() {
		// Обновление состояния - изменение текста подсказки
		this.setState( { helpText: "Changet" } )
		// Вывод сообщения в консоль
		console.log( "Clicked" )
	}

	// Метод обработки наведения мыши на поле ввода
	mouseOver() {
		// Вывод сообщения в консоль при наведении мыши
		console.log( "Mouse Over" )
	}
}

// Экспорт компонента для использования в качестве корневого
export default App



// Краткое описание функциональности:
// Компонент App:

// Управляет состоянием приложения (текст подсказки и данные пользователя)

// Содержит поле ввода с обработчиками событий

// Отображает различные компоненты (Header, CustomImage)

// Реагирует на действия пользователя (клики, ввод текста, наведение мыши)

// Использует условный рендеринг для отображения разного контента
